import React from 'react';
import './pageComments.scss';
import UserLogo from '../../assets/Images/Mohan-muruge.jpg'


const PageComments = () => {

    return (
    <section className="comments">
        <h3 className="comments__header">3 Comments</h3>
        <form className="comments__form">
            <img className="comments__form-user-image" src={UserLogo} alt="user-image"></img>
            <label className="comments__form-comment-label" name="comment-entry-field">JOIN THE CONVERSATION</label>
            <textarea className="comments__form-comment-field" placeholder="Write comment here"></textarea>
            <button className="comments__form-button"> COMMENT</button>
        </form>

        <div className="comments__posted">

        </div>

    </section>
    )}

    let enteredComments = [

        {
            name: "Michael Lyons",
            date: "12/18/2018",
            comment:`They BLEW the ROOF off at their
            last show, once everyone started
            figuring out they were going. This is
            still simply the greatest opening of a
            concert I have EVER witnessed.`
        },

        {
            name: "Gary Wong",
            date: "12/12/2018",
            comment:`Every time I see him shred I feel so
            motivated to get off my couch and
            hop on my board. He’s so talented! I
            wish I can ride like him one day so I
            can really enjoy myself!`
        },

        {
            name: "Theodore Duncan",
            date: "11/15/2018",
            comment:`How can someone be so good!!!
            You can tell he lives for this and
            loves to do it every day. Everytime I
            see him I feel instantly happy! He’s
            definitely my favorite ever!`
        },    
    ];

    const EnteredComments = () => {

        return (
            <div className="entered-comment">
                {enteredComments.name}
                {enteredComments.comment}
            </div>
        )

    }

    function CommentsDisplayed() {
        const pageComments = enteredComments.map((comment) =>
        renderComments(comment));
        return <div className="comment-entered">{pageComments}</div>;

    }


export default PageComments;
